document.addEventListener("DOMContentLoaded",function(){const e=new Date;document.querySelectorAll("time.auction-end-time").forEach(t=>{const n=new Date(t.getAttribute("datetime")),d=t.closest("a");n<=e?(d.innerHTML=`<s>${d.textContent}</s>`,d.classList.add("ended")):n-e<=36e5&&(d.innerHTML=`⏰ ${d.textContent}`,d.classList.add("ending"))})});