function updateTimeAgo(){const e=document.getElementById("last-updated"),t=e.getAttribute("datetime"),o=new Date(t),n=new Date,a=Math.floor((n-o)/1e3);let d;if(a<60)d=`${a} seconds ago`;else if(a<3600){const e=Math.floor(a/60);d=`${e} minute${1!==e?"s":""} ago`}else if(a<86400){const e=Math.floor(a/3600);d=`${e} hour${1!==e?"s":""} ago`}else{const e=Math.floor(a/86400);d=`${e} day${1!==e?"s":""} ago`}e.textContent=d}document.addEventListener("DOMContentLoaded",function(){updateTimeAgo(),setInterval(updateTimeAgo,6e4)});