function updateTimeAgo(){const e=document.getElementById("last-updated"),t=e.getAttribute("datetime"),n=new Date(t),o=new Date,i=Math.floor((o-n)/1e3);let a;if(i<60)a=`${i} seconds ago`;else if(i<3600){const e=Math.floor(i/60);a=`${e} minute${1!==e?"s":""} ago`}else if(i<86400){const e=Math.floor(i/3600);a=`${e} hour${1!==e?"s":""} ago`}else{const e=Math.floor(i/86400);a=`${e} day${1!==e?"s":""} ago`}e.textContent=a}function updateExpiredLinks(){const e=document.querySelectorAll("time.endtime");e.forEach(function(e){const t=new Date(e.getAttribute("datetime")),n=new Date;if(n>t){const t=e.previousElementSibling;t.style.textDecoration="line-through"}})}document.addEventListener("DOMContentLoaded",function(){updateTimeAgo(),setInterval(updateTimeAgo,6e4)}),document.addEventListener("DOMContentLoaded",function(){updateExpiredLinks(),setInterval(updateExpiredLinks,3e5)});