function updateTimeAgo(){const e=document.getElementById("last-updated"),t=e.getAttribute("datetime"),n=new Date(t),o=new Date,a=Math.floor((o-n)/1e3);let i;if(a<60)i=`${a} second${1!==a?"s":""} ago`;else{const e=Math.floor(a/60),t=Math.floor(e/60),n=Math.floor(t/24);i=n>0?`${n} day${1!==n?"s":""}${t%24>0?`, ${t%24} hour${t%24!=1?"s":""}`:""} ago`:t>0?`${t} hour${1!==t?"s":""}${e%60>0?`, ${e%60} minute${e%60!=1?"s":""}`:""} ago`:`${e} minute${1!==e?"s":""} ago`}e.textContent=i}function updateExpiredLinks(){const e=document.querySelectorAll("time.endtime");e.forEach(function(e){const t=new Date(e.getAttribute("datetime")),n=new Date;if(n>t){const t=e.previousElementSibling;t.style.textDecoration="line-through"}})}document.addEventListener("DOMContentLoaded",function(){updateTimeAgo(),setInterval(updateTimeAgo,6e4)}),document.addEventListener("DOMContentLoaded",function(){updateExpiredLinks(),setInterval(updateExpiredLinks,3e5)});